mapper.select:
SELECT DISTINCT b.`parentid` sectionParentid,c.`attrname` sectionName FROM tb_medical_hospital_section a,tb_medical_section b,tb_medical_section c
1=1 and a.`sectionID`=b.`id` AND b.`parentid`=c.`id`
and a.hospitalID=#{hospitalID}

SELECT a.*,b.`attrname` sectionName,b.`parentid` sectionParentid FROM tb_medical_hospital_section a,tb_medical_section b
1=1 and a.`sectionID`=b.`id`
and a.hospitalID=#{hospitalID}
