mapper.select:
SELECT * FROM tb_medical_doctor_fee t
WHERE t.doctorID = #{doctorID}

	and t.type = #{type}

SELECT count(1) FROM tb_medical_doctor_fee t
WHERE t.doctorID = #{doctorID}

	and t.type = #{type}

mapper.update:
UPDATE tb_medical_doctor_fee SET  
levelTitle=#{levelTitle},
fee=#{fee}
WHERE id=#{id}
