mapper.select:
SELECT t.*, t2.doctorName creatorName FROM tb_medical_record t left join tb_medical_patient t1 on t.patientID = t1.patientID left JOIN tb_medical_doctor t2 on t.creator = t2.loginID and t.source = 2 WHERE t.creator = ${creator}
and t.patientName like CONCAT('%', '${patientName}', '%')
and t.patientCard like CONCAT('%', '${patientCard}', '%')

SELECT count(1) FROM tb_medical_record t left join tb_medical_patient t1 on t.patientID = t1.patientID left JOIN tb_medical_doctor t2 on t.creator = t2.loginID and t.source = 2 WHERE t.creator = ${creator}
and t.patientName like CONCAT('%', '${patientName}', '%')
and t.patientCard like CONCAT('%', '${patientCard}', '%')
